var APP = APP || {};

APP.index = (function (APP) {

  function bindEvents() {
    console.log('index module has initiated');

  }

  function init() {
    bindEvents();
  }

  return {
    init: init
  };

})(APP);

APP.router = (function(APP) {

  function init() {

    // ------------------
    //  AUTO START
    // ------------------

    APP.index.init();

    // ------------------
    //  CONDITIONAL START
    // ------------------

  }

  return {
    init: init
  };

})(APP);
APP.router.init();
